    var y1         = this.vbf[baseOffset+1];
    var x2         = this.vbf[baseOffset+2];
    var y2         = this.vbf[baseOffset+3];
    var x3         = this.vbf[baseOffset+4];
    var y3         = this.vbf[baseOffset+5];

    callback.onOneTriangle(x1, y1, x2, y2, x3, y3, vpId);
