        // Changing textures (blue highlighting for home and roll arrows)
        changingTextures.push(loadTexture(getResourceUrl(resRoot + 'VChomeS.png')));
        changingTextures.push(loadTexture(getResourceUrl(resRoot + 'VCarrowsS0.png')));
