    var stack = []; // The hotkey stack

    var keys = []; // The keys that are currently held

    // Pending items
    var onPressQueue = [];
    var onReleaseQueue = [];

    /**
     * Enum for keycodes.
     * @readonly
     * @enum {number}
     */
    var KEYCODES = Autodesk.Viewing.KeyCode;

    var _names = ["hotkeys"];

    function getNames()
    {
        return _names;
    }

    function getName()
    {
        return _names[0];
    }

